mapper.insert:
INSERT INTO Bill (userId, billSubject, billAmount
, billDate
, billStatus)
VALUES (#{userId}, #{billSubject}, #{billAmount}
, #{billDate}
, #{billStatus})

mapper.update:
UPDATE Bill
SET billStatus = #{billStatus}
WHERE billId = #{billId}

mapper.select:
SELECT userId
FROM Bill
WHERE billId = #{billId}
