mapper.select:
SELECT borrowingId,userId,instanceId,borrowDate,dueDate,lateRetDate,borrowAprvStatus,lateRetAprvStatus,returnDate
FROM Borrowing


        AND borrowingId=#{borrowingId}


        AND userId=#{userId}


        AND instanceId=#{instanceId}


        AND borrowDate=#{borrowDate}


        AND dueDate=#{dueDate}


        AND lateRetDate=#{lateRetDate}


        AND borrowAprvStatus=#{borrowAprvStatus}


        AND lateRetAprvStatus=#{lateRetAprvStatus}

mapper.update:
UPDATE Borrowing
lateRetDate = #{lateRetDate}, borrowAprvStatus = #{borrowAprvStatus}, lateRetAprvStatus = #{lateRetAprvStatus}, returnDate = #{returnDate},
WHERE borrowingId = #{borrowingId}
